static size_t speed_idx = 1;
static volatile size_t sec = 0;
static volatile size_t state = 0;
static volatile bool stopped = false;
static volatile bool redraw = true;

void TMR0_IRQHandler(void)
{
    if (!stopped) {
        state = (state + 1) % 6;
        redraw = true;
    }

    TIMER_ClearIntFlag(TIMER0); // Clear Timer0 time-out interrupt flag
}

void TMR1_IRQHandler(void)
{
    if (!stopped) {
        if (sec == 9999)
            sec = 0;
        else
            sec++;
    }

    TIMER_ClearIntFlag(TIMER1); // Clear Timer0 time-out interrupt flag
}

void Init_Timer0(void)
{
    TIMER_Open(TIMER0, TMR0_OPERATING_MODE, hz[speed_idx]);
    TIMER_SET_PRESCALE_VALUE(TIMER0, scale[speed_idx]);
    TIMER_EnableInt(TIMER0);
    NVIC_EnableIRQ(TMR0_IRQn);
    TIMER_Start(TIMER0);
}

void Init_Timer1(void)
{
    TIMER_Open(TIMER1, TMR1_OPERATING_MODE, TMR1_OPERATING_FREQ);
    TIMER_EnableInt(TIMER1);
    NVIC_EnableIRQ(TMR1_IRQn);
    TIMER_Start(TIMER1);
}

void KeyPadRisingEdge(void (*func)(uint8_t))
{
    static uint8_t last_state = 0;
    uint8_t read1 = ScanKey();

    if (last_state != read1)
    {
        // Pressed
        CLK_SysTickDelay(25000);

        uint8_t read2 = ScanKey();

        if (read2 == read1)
            last_state = read2;

        if (last_state)
            func(last_state);
    }
}

